import './App.css';
import React ,{useRef, useState} from 'react';
import logo from  './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';
function App() {
 let [currentIndex , setCurrentIndex] = useState(0);
 const imgRef = useRef("");
 const images = [hero1,hero2,hero3,hero4,hero5];
 let i = 0;
 function slideLeft(){
  if(i > 0){
    i--;
    setCurrentIndex(i);
    console.log("print")
  }
  };

  function slideRight(){
    if(i < images.length-1){
      i++;
      setCurrentIndex(i);
      console.log("print")
    }
  }

  return (
    <>
   <div className="container">
    <nav>
      <div className="logo">
    <img src={logo} alt="error" />
      </div>
        <ul>
          <li>Home</li>
          <li>About Us</li>
          <li>Our Services</li>
          <li>Testimonials</li>
          <li>Blogs</li>
          <li>Dropdown <span></span></li>
          <li>Contact</li>
          </ul>
    </nav>
    <button onClick={slideLeft}>Left</button>
    <button onClick={slideRight}>Right</button>
    <div className="imgCollection" ref={imgRef}>
     <img key={i} src={images[currentIndex]} alt="not found" draggable="true"/>

    </div>
   </div>
    </>
  );
}

export default App;
