import './App.css';
import React ,{useEffect, useRef, useState} from 'react';
import logo from  './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';
function App() {
 let [position , setPosition] = useState(0);
 const imgRef = useRef("");
//  let imgColl = document.getElementsByClassName("imgCollection");
 function slideLeft (){
   setPosition((pray)=>{pray-300});
  };

  function slideRight(){
    setPosition((pray)=>{pray+300});
  }
 useEffect(()=>{
 imgRef.current.style.transform = `translateX(${position}px)`;

 },[position])


  return (
    <>
   <div className="container">
    <nav>
      <div className="logo">
    <img src={logo} alt="error" />
      </div>
        <ul>
          <li>Home</li>
          <li>About Us</li>
          <li>Our Services</li>
          <li>Testimonials</li>
          <li>Blogs</li>
          <li>Dropdown <span></span></li>
          <li>Contact</li>
          </ul>
    </nav>
    <button onClick={slideLeft}>Left</button>
    <button onClick={slideRight}>Right</button>
    <div className="imgCollection" ref={imgRef}>
     <img src={hero1} alt="error" />
     <img src={hero2} alt="error" />
     <img src={hero3} alt="error" />
     <img src={hero4} alt="error" />
     <img src={hero5} alt="error" />
    </div>
   </div>
    </>
  );
}

export default App;
