import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import gsap from 'gsap';
import logo from './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';

function App() {
  // State for the current image index
  const [varL, setVarL] = useState(0);
  const [varR, setVarR] = useState(0);
  const imgRef = useRef();
  // Images array
  const images = [hero1, hero2, hero3, hero4, hero5];

  // Function to handle the left slide

    const slideLeft = () => {
      // console.log(window.innerWidth);
     setVarL(varL-window.innerWidth)
      gsap.to(imgRef.current,{
        x:varL,
        duration:1,
        delay:0.5
     })
    };

    // Function to handle the right slide
    const slideRight = () => {
      setVarR(varR+window.innerHeight);
      gsap.to(imgRef.current,{
        x:varR,
        duration:1,
        delay:0.5
     })
    };


  return (
    <>
      <div className="container">
        <nav>
          <div className="logo">
            <img src={logo} alt="Logo" />
          </div>
          <ul>
            <li>Home</li>
            <li>About Us</li>
            <li>Our Services</li>
            <li>Testimonials</li>
            <li>Blogs</li>
            <li>Dropdown <span></span></li>
            <li>Contact</li>
          </ul>
        </nav>

        <div className="img-slider">
          <button onClick={slideLeft}>Left</button>
          <button onClick={slideRight}>Right</button>

          {/* Image display */}
          <div className="imgCollection" ref={imgRef}>
            {
              images.map((img,index)=>(
                <img key={index} src={img} alt="error" /> 
              ))
            }
          </div>

        </div>
      </div>
    </>
  );
}

export default App;
