import './App.css';
import React, { useRef, useState } from 'react';
import gsap from 'gsap';
import logo from './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';

function App() {
  // State to manage position of the images
  const [position, setPosition] = useState(0);
  const imgRef = useRef(null);

  const images = [hero1, hero2, hero3, hero4, hero5];
  let i = 0;

  let tl = gsap.timeline();
   tl.from("ul",{
    y:-100,
    duration:2,
    delay:0.5
   })

  const slideLeft = () => {
    const newPosition = position + window.innerWidth;
    setPosition(newPosition); // Update state
    gsap.to(imgRef.current, {
      x: newPosition,
      duration: 1,
      delay: 0.1,
    });
  }

  // Slide right (move to the left direction)
  const slideRight = () => {
    const newPosition = position - window.innerWidth;
    setPosition(newPosition); 
    gsap.to(imgRef.current, {
      x: newPosition,
      duration: 1,
      delay: 0.1,
    });
  }
  return (
    <>
      <div className="container">
        <nav>
          <div className="logo">
            <img src={logo} alt="Logo" />
          </div>
          <ul>
            <li>Home</li>
            <li>About Us</li>
            <li>Our Services</li>
            <li>Testimonials</li>
            <li>Blogs</li>
            <li>Dropdown <span></span></li>
            <li>Contact</li>
          </ul>
        </nav>

        <div className="img-slider">
          <button onClick={slideLeft}>Left</button>
          <button onClick={slideRight}>Right</button>

          {/* Image display */}
          <div className="imgCollection" ref={imgRef} style={{ display: 'flex' }}>
            {images.map((img, index) => (
              <img key={index} src={img} alt="Slide" style={{ width: '100vw', flexShrink: 0 }} />
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
