import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import logo from './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';

function App() {
  let [position,setPosition] = useState(0);
  let imgRef = useRef();
  const images = [hero1,hero2,hero3,hero4,hero5];
  console.log(images[1])
  function slideLeft(){
    setPosition((p)=>p-300);
  }
  function slideRight(){
    setPosition((p)=>p+300)
  }
  useEffect(()=>{
    if(imgRef.current){
      imgRef.current.style.transform = `translateX(${position}px)`;
    }
  },[position])


  return (
    <>
      <div className="container">
        <nav>
          <div className="logo">
            <img src={logo} alt="error" />
          </div>
          <ul>
            <li>Home</li>
            <li>About Us</li>
            <li>Our Services</li>
            <li>Testimonials</li>
            <li>Blogs</li>
            <li>Dropdown <span></span></li>
            <li>Contact</li>
          </ul>
        </nav>

        <button onClick={slideLeft}>Left</button>
        <button onClick={slideRight}>Right</button>

        <div className="imgCollection" ref={imgRef} style={{ display: 'flex', transition: 'transform 0.5s ease' }}>
          {images.map((img,index)=>{
           <img key={index} src={img} alt='error' />
          })}
        </div>
      </div>
    </>
  );
}

export default App;
