import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import logo from './img/logo.png';
import hero1 from './img/hero_1.jpg';
import hero2 from './img/hero_2.jpg';
import hero3 from './img/hero_3.jpg';
import hero4 from './img/hero_4.jpg';
import hero5 from './img/hero_5.jpg';

function App() {
  const [position,setPosition] = useState(0);
  const imgRef = useRef(null);
  const images = [hero1,hero2,hero3,hero4,hero5];
  // function slideLeft(){
  //   setPosition((p)=>Math.min());
  // }
  // function slideRight(){
  //   setPosition((p)=>p+300)
  // }
    // Function to slide left
    const slideLeft = () => {
      setPosition((prev) => Math.min(prev + 300, 0)); // Limit sliding to left boundary
    };

    // Function to slide right
    const slideRight = () => {
      const maxPosition = -300 * (images.length - 1); // Calculate max slide based on the number of images
      setPosition((prev) => Math.max(prev - 300, maxPosition)); // Slide right
    };
  useEffect(()=>{
    if(imgRef.current){
      imgRef.current.style.transform = `translateX(${position}px)`;
    }
  },[position]);

  return (
    <>
      <div className="container">
        <nav>
          <div className="logo">
            <img src={logo} alt="error" />
          </div>
          <ul>
            <li>Home</li>
            <li>About Us</li>
            <li>Our Services</li>
            <li>Testimonials</li>
            <li>Blogs</li>
            <li>Dropdown <span></span></li>
            <li>Contact</li>
          </ul>
        </nav>

        <button onClick={slideLeft}>Left</button>
        <button onClick={slideRight}>Right</button>

        <div className="imgCollection" ref={imgRef} style={{ display: 'flex', transition: 'transform 0.5s ease' }}>
          {images.map((img,index)=>(
           <img key={index} src={img} alt='error' />
          ))}
        </div>
      </div>
    </>
  );
}

export default App;
